  logo::logo() : mPtr ( mLogoStr ) {}

  logo::~logo()
  {
    std::cout << std::endl;
  }

  void logo::operator++ ( int aDummy )
  {
    if ( *mPtr == '\0' )
    {
      mPtr = mLogoStr;
    }

    std::cout << *mPtr++ << std::flush;
  }


